hljs.registerLanguage("html", (() => {
    "use strict";
    return (e) => ({
        name: "HTML",
        aliases: ["htm", "html5"],
        case_insensitive: true,
        contains: [
            {
                className: "doctype",
                begin: /<!DOCTYPE/,
                end: />/
            },
            {
                className: "comment",
                variants: [
                    { begin: /<!--/, end: /-->/ },
                    { begin: /<!\[/, end: /\]>/ }
                ]
            },
            {
                className: "tag",
                variants: [
                    { begin: /<style(?=.*<\/style>)?/, end: /<\/style>/ },
                    { begin: /<script(?=.*<\/script>)?/, end: /<\/script>/ },
                    { begin: /<script(?=.*>)/, end: />/ },
                    { begin: /<[\w-]+/, end: />/ }
                ],
                contains: [
                    {
                        className: "attribute",
                        begin: /\{\{/,
                        end: /\}\}/
                    },
                    {
                        begin: /=/, end: /=/
                    }
                ]
            }
        ]
    })
})());
